from typing import List
import time


class Solution:
    # n^2
    def threeSum(self, nums: List[int]) -> List[List[int]]:
        length = len(nums)
        if length < 3:
            return []
        if length == 3:
            return [nums] if sum(nums) == 0 else []

        # nlog(n)
        nums.sort()
        sets = []

        # n^2
        for i in range(length-2):
            # Remove duplicated
            if i > 0 and nums[i-1] == nums[i]:
                continue
            j, k = i+1, length-1
            while j < k:
                x = nums[i]
                y = nums[j]
                z = nums[k]

                s = x+y+z
                if s < 0:
                    j += 1
                elif s > 0:
                    k -= 1
                else:
                    sets.append([x, y, z])
                    # Remove duplicated
                    while j < k and nums[j] == nums[j+1]:
                        j += 1
                    while j< k and nums[k] == nums[k-1]:
                        k -= 1
                    j += 1
                    k -= 1

        return sorted(sets)


