for A,  r in tests:
    start_time = time.time()
    a = Solution().threeSum([*A])
    exec_time = (time.time() - start_time)
    if a != r or exec_time > 1:
        print(f"\033[91m -> {a} # {r} | {exec_time}")
    else:
        print(f"\033[92m -> OK | {exec_time}")
